import { expect } from 'chai'
import { problem008 } from '../src/problems/problem008'

const sequence = ''

describe('Problem 008', function () {
  it('Should return 5832 as the highest product of 4 adjecent digits of given 1000-digit number.', function () {
    expect(problem008(sequence, 4)).to.equal(5832)
  })
  it('Should return 9 as the highest product of 1 adjecent digits of given 1000-digit number.', function () {
    expect(problem008(sequence, 1)).to.equal(9)
  })
  it('Should return 81 as the highest product of 2 adjecent digits of given 1000-digit number.', function () {
    expect(problem008(sequence, 2)).to.equal(81)
  })
  it('Should return 23514624000 as the highest product of 13 adjecent digits of given 1000-digit number.', function () {
    expect(problem008(sequence, 13)).to.equal(23514624000)
  })
})
